INSERT INTO `adventuredb`.`tours` (`start_location`, `end_location`, `start_date`, `end_date`, `cost`, `tour_description`, `num_avail`, `tour_name`, `continent`, `tour_image`, `map_image`) VALUES ('Beijing, China', 'Shanghai, China', '2015-04-11', '2017-10-07', '0', 'While retracing the footsteps of ancient warriors along the Great Wall, you realize you’re onto something. You want to get into the heart of the Middle Kingdom and our CEO will take you ‘over the wall’. Handpicked for their expertise, they’ll bring you deep into China and walk backward in time with you to Tibet where you’ll reconnect with ancient ways long forgotten by the modern world. You'll have the chance to visit and have lunch at the village where the Terracotta warriors were unearthed, and experience exotic local cuisine. You won’t just experience China’s arts and culture, you’ll become a part of it.' , '50' , 'China, Yangtze and Tibet Explorer' , 'Asia' , 'https://media.gadventures.com/media-server/cache/46/d7/46d779bca8b3e12c40f7170bc257a71d.jpg' , 'https://media.gadventures.com/media-server/cache/46/d7/46d779bca8b3e12c40f7170bc257a71d.jpg' );
